def find_longest_common_subsequence(s1, s2):
    # initialize the result to be an empty list
    result = []
    # create an empty matrix to store the dynamic programming results
    dp = [[0] * len(s1) for _ in range(len(s2) + 1)]
    for i in range(len(s1)):
