public static boolean isPowerOfThree(int n) {
    // base case
    if (n == 0) {
        return false;
    }

    // recurse until n has no more factors of 3 left
    while (n % 3 == 0) {
        n /= 3;
    }

    // check if n is a power of 3 by comparing it to 3^x where x = floor(log(n,3))
    int x = (int) Math.floor(Math.log(n, 3));
    if (n == (int) Math.pow(3, x)) {
        return true;
    } else {
        return false;
    }
}

