std::string convert_markdown_to_html(std::string texto) {
    // Se hace uso del formato \`x\` para representar literalmente x en el html
    // Se hace uso de `\n` para representar un salto de línea
    // Se hace uso de `\t` para representar tabulaciones en el html
    std::string html = texto;
    for (auto & c : html) {
        if (c == '`' && html.size() > 1) {
            html[html.size() - 1] = '\n'; // eliminamos el backslash
        } else if (c == '\n') {
            html += '\n'; // Agregamos un salto de linea en el HTML
        } else if (c == '\t') {
            html += '\t'; // Agregamos una tabulacion en el HTML
        } else {
            html += c; // agregamos el carácter sin modificarlo.
        }
    }
    return html; // Devolvemos el resultado como un string.
}
