from queue import Queue

class Stack(object):
    def __init__(self):
        self.top = None
        self.stack = Queue()
        self.bottom = Queue()

    def push(self, item):
        self.stack.put(item)

    def pop(self):
        if not self.stack.empty():
            return self.stack.get()
        else:
            item = self.bottom.get()
            self.bottom.put(None)
            return item

    def peek(self):
        if not self.stack.empty():
            return self.stack.get()
        else:
            return None  # if no item on stack, we look at the bottom of the stack instead and return its value 
