```lean4
def timsort(coll : Seq[T]): Seq[T] = {
  val res = coll.iterator.toList
  val n = res.size
  var i = 0
  var j = 0
  while i < n {
    while j < n && res(j).compareTo(res(i)) > 0 {
      j += 1
    }
    if j < n {
      res(i) = res(j)
      i += 1
      j += 1
    } else {
      i += 1
    }
  }
  res.toSeq
}
```
