```lean
fun sort<T>(xs: Array[T], cmp: Comparer<T>): Array[T] {
  var i = 0; // left
  var j = xs.len - 1; // right

  while i < j {
    // i: leftmost element that's smaller than j, or equal to it if empty.
    // j: rightmost element that's smaller than i, or equal to it if empty.
    while i < j && cmp(xs[i], xs[j]) <= 0 {
      i += 1; // move left element to the right.
    }

    while j >= i && cmp(xs[j], xs[i]) <= 0 {
      j -= 1; // move right element to the left.
    }

    if i >= j { return xs } // if the array is sorted, return it.

    xs[i] <- xs[j] // swap elements at position i and j.
    i += 1; // move left element to the right.
    j -= 1; // move right element to the left.
  }

  return xs // sort succeeded! Return the sorted array.  
}

