from typing import Dict

class Solution:
    def solve(self, board: List[List[str]], word: str) -> List[str]:
        words = [word]
        n = len(board)
        m = len(board[0])
        res = []
        for i in range(n):
            for j in range(m):
                if board[i][j] == 'X':
                    continue
                if word in board[i][j]:
                    words.append(board[i][j])
                elif words[-1] in board[i][j]:
                    del words[-1]
                else:
                    return [i + 1, j + 1, i + m - 1, j + m - 1, i + n - 1, j + n - 1, i + n - m, j + n - m] + res # move to end of the queue and add current position to end of queue. res is empty so start from position given by the move and add the position to the queue. This is for backtracking.
