        // If the target word is found, increment its corresponding count in the vector.
        if (word == "target_word") {
            wordCounts[word]++;
        } else {
            // Otherwise, add it to the vector as is.
            wordCounts[word] = 1;
        }
    }

    // Print out the number of occurrences of each word in the text file. 
    for (int i = 0; i < wordCounts.size(); i++) { 
        cout << i << ": " << wordCounts[i] << endl; 
    } 
} 
