#include <string>
#include <cstdlib>
#include <iostream>

int main() {
    std::string word = "hello";
    std::ifstream file("input.txt"); // open file for reading
    std::string line; // read line by line from file
    int counter = 0; // counter for word frequency count

    while (std::getline(file, line)) { // read each line from file
        for (auto c : line) { // iterate over characters in line
            if (c == word[0]) { // check if current character is first character of word
                counter++; // increment counter for word frequency count if so
            }
        }
    }
    std::cout << "The number of times " << word << " occurs in the input file is " << counter << std::endl; // print the word frequency count to standard output
}
