def find_longest_common_subsequence(s1, s2):
    dp = [[0] * len(s1) for _ in range(len(s2))]
    for i in range(len(s1)):
        for j in range(len(s2)):
            if s1[i] == s2[j]:
                dp[i][j] = 1 + max(dp[i-1][j], dp[i][j-1])
            else:
                dp[i][j] = 0
    return max([len(k) for k in dp])

