```cpp
std::string convert_markdown_to_html(const std::string& input) {
  std::string output = "";
  // Replace all newlines with <br> tags
  for (size_t i = 0; i < input.size(); ++i) {
    if (input[i] == '\n') {
      output += "<br>";
    } else {
      output += input[i];
    }
  }
  // Convert Markdown to HTML using a markdown parser and transformer
  pugi::xml_document doc;
  pugi::xml_parse_result result = pugi::xml_parse(doc, input.c_str());
  if (result.status != pugi::status_ok) {
    throw std::runtime_error("Error parsing Markdown: " + result.description);
  }
  // Walk the document and replace the new <p> tags with <div> tags.
  // This is done because the markdown parser doesn't insert paragraph tags, but the HTML transformer does.
  for (auto& elem : doc.child("body")) {
    for (auto& child : elem) {
      if (child.name() == "p") {
        child = pugi::node(pugi::node_pcdata); // Replace the <p> tag with a <div> tag.
      } else if (child.name() == "div") { // If we already have a <div> tag, don't modify it.
        if (child.first_child()) { continue; } // If we already have a child, don't modify it.
      } else if (child.name() == "span" && child.first_child()) { // If we have a <span> tag with a child, modify it to have no children.
        child.remove_child(); child = pugi::node(pugi::node_pcdata); // Remove the child and add a <div> tag to its place.
