```cpp
#include <iostream>
#include <fstream>
#include <string>
#include <vector>
#include <algorithm>

using namespace std;

// read lines from a text file, one per line
ifstream input("input.txt");

// read each line and add the keyword to the counter vector, ignoring empty lines and lines starting with '#' (comment)
std::vector<int> counter(0);
int word = 0;
string line;
while (getline(input, line)) {
    if (line.empty() || line.front() == '#') continue; // ignore empty lines and comments
    word = 0; // reset the counter for each new line
    while (isspace(line[word])) ++word; // skip leading spaces in each line
    if (line[word] == keyword) ++counter[word]; // increment the counter for each occurrence of keyword on that line
}
