def find_common_elements(a, b):
    # Initialize a set of common elements
    common = set()

    # Iterate over both arrays and add any matching elements to the set
    for i in range(len(a)):
        if a[i] == b[i]:
            common.add(a[i])

    # Return the common elements in the set
    return list(common)
