#include <iostream>
#include <string>

using namespace std;

int main() {
    string line;
    getline(cin, line);
    size_t count[256]; // store count of each character in the line in this array
    for (char& c : line) {
        count[c]++; // increment the count of each character in the line by one
    }
    for (char& c : line) {
        if (count[c] > 1) { // if there are two or more occurrences of a character in the line, print it
            cout << c << " "; // and space it out, so that the count doesn't get overlaid with the next character when we print it.
            for (int i = 0; i < count[c]; i++) { // print its count as many times as it occurs in the line.
                cout << " " << i + 1; // space out the number, so that it doesn't get overlaid with the next character when we print it.
            }
        } else { // if there's only one occurrence of a character in the line, print it normally.
            cout << c; // no need to space it out.
        }
    }
}
