```C++
#include <fstream>
#include <string>
#include <iostream>

using namespace std;

int main() {
    ifstream in("text.txt"); // Open the input file.
    if (!in) { // If the file cannot be opened.
        cerr << "Error opening file" << endl;
        return -1; // Return an error code.
    }
    string line, word; // Local variables to store lines and words.
    int count = 0; // Count how many times the word occurs.
    while (getline(in, line)) { // Read lines from the file one at a time.
        for (auto iter = line.begin(); iter != line.end(); ++iter) { // Loop over each character in the line.
            if (*iter == ' ' && word != "") { // If we're on a space, store the word.
                word += *iter; // Append it to the current word, if any.
            } else if (*iter == word[0]) { // If the character matches the current word, increment its count.
                ++count;
            } else { // Otherwise, clear the current word and start a new one.
                word = "";
            }
        }
        if (word != "") { // If we have a non-empty word, print it out.
            cout << "The word '" << word << "' occurred " << count << " times." << endl; // Print out the word and its count.
        } else { // Otherwise, print out nothing.
            cout << endl; // Skip a line.
        }
    }    
    return 0; // Return an error code if there was an error reading the file. Otherwise return zero (indicating success).

