def longest_common_subsequence(x: str, y: str) -> str:
    x_length, y_length = len(x), len(y)
    dp = [[0] * x_length for _ in range(y_length)]  # init
    for i in range(x_length):  # dijkstra
        for j in range(y_length):
            if x[i] == y[j]:
                dp[i][j] = 1 + dp[i-1][j-1] if i > 0 and j > 0 else 0
            else:
                dp[i][j] = max(dp[i-1][j], dp[i][j-1])
    return "".join(list(filter(lambda x: x == 1, dp)))  # най-дългата обща подпоследователност
