function binarySearch(arr: Array<number>, target: number): boolean {
    const level = 0; // Especifica la posició en què ha començat la cerca
    let current = arr[level]; // Obté el valor del primer element de l'array
    let lower = level; // Especifica el nivell inferior del nivell actual
    let upper = level; // Especifica el nivell superior del nivell actual

    while (current <= upper) { // Realitza una iteració fins que es pugui completar la comparació
        if (current === target) { // Si el valor actual és igual al valor de la cerca
            return true; // L'element s'ha trobat
        } else if (target < current) { // Si el valor actual és més petit que el valor de la cerca
            upper--; // Canvia el nivell superior per l'element immediatament inferior en l'array
