            lower++; // Canvia el nivell inferior per l'element immediatament superior en l'array
        } else { // Si no hi ha una diferència entre els valors
            return false; // L'element no s'ha trobat i la cerca continua
        }
        current++; // Avança al següent element de l'array
    } 
    return false;  // L'element no s'ha trobat i la cerca finalitza amb fracàs. 
