def timsort[M[_]: Monoid](a: List[M[_]]): List[M[_]] = {
  def sort(m: M[_], i: Int, j: Int): Int = {
    var x = m[i]
    var y = m[j]
    var t = 0
    while (i <= j) {
      if (t == 0) {
        if (x <= y) t = -1; else t = 1;
      } else if (t == -1) {
        if (x <= y) i += 1; else j -= 1;
      } else if (t == 1) {
        if (x >= y) i += 1; else j -= 1;
