import java.math.BigInteger;
import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        int n = scanner.nextInt();
        System.out.println(isPowerOfThree(n) ? "true" : "false");
    }

    public static boolean isPowerOfThree(int n) {
        BigInteger three = BigInteger.valueOf(3);
        BigInteger x = BigInteger.ONE;
        if (n == Integer.MIN_VALUE) {
            return false;
        } else if (n == Integer.MAX_VALUE) {
            return true;
        } else {
            while (x.compareTo(three) < 0) { // while n != 3^x, x++... else while n != Integer.MIN_VALUE, x++... else while n != Integer.MAX_VALUE, x++...
                if (x.add(BigInteger.ONE).equals(three)) { // (3^x) % x == 0 and x != Integer.MIN_VALUE, x++... else if (x.add(BigInteger.ONE).equals(three)) { // (3^x) % x == 0 and x != Integer.MAX_VALUE, x++... else if (x.add(BigInteger.ONE).equals(three)) { // (3^x) % x == 0 and x != Integer.MIN_VALUE || x != Integer.MAX_VALUE, x++... else if (x.add(BigInteger.ONE).equals(three)) { // (3^x) % x == 0 and x == Integer.MIN_VALUE or x == Integer.MAX_VALUE, x++... else return false; // no such number exists...} } } } } return true; // n is a power of three...}
    }
}
```

