std::string convertir_markdown_a_html(const std::string& input) {
  // replace all backslashes with HTML entities
  std::replace(input.begin(), input.end(), '\\', '\\\\');

  // replace all line breaks with <br> tags
  std::replace(input.begin(), input.end(), '\n', '<br>');

  // convert all headers to HTML <h1>-<h6> tags
  const std::vector<std::string> header_levels = {"h1", "h2", "h3", "h4", "h5", "h6"};
  for (auto& header_level : header_levels) {
    auto pos = input.find(header_level);
    if (pos != std::string::npos) {
      input.replace(pos, header_level.size(), header_level, "<" + header_level + ">");
    }
  }

  // convert all image urls to <img> tags with src attribute set to the url of the image
  for (auto& image : image_urls) {
    auto pos = input.find(image);
    if (pos != std::string::npos) {
      input.replace(pos, image.size(), image, "<img src=\"" + image + "\">");
    }
  }

  // replace all link urls with <a href=""> tags and href attribute set to the url of the link
  for (auto& link : link_urls) {
    auto pos = input.find(link);
    if (pos != std::string::npos) {
      input.replace(pos, link.size(), link, "<a href=\"" + link + "\">");      
