def common_longest_subsequence(s1, s2):
    """Retourne une chaîne représentant la plus longue sous-séquence commune entre s1 et s2."""
    n1, n2 = len(s1), len(s2)
    if n1 == 0 or n2 == 0:
        return s1, s2
    L = [0] * (n2 + 1)
    for i in range(n1):
