```lean
fn timsort(xs: Array[T]): Array[T] {
    let swap_i, j = 0, 0
    while i < xs.len() && j < xs.len() {
        if xs[i] < xs[j] {
            swap_i, j = i, j
        } else if xs[i] > xs[j] {
            swap_i, j = i, j
        } else {
            i += 1
            j += 1
        }
    }
    if i < xs.len() { swap_i, i = i, i + 1 } else if j < xs.len() { swap_j, j = j, j + 1 }
    for i in 0..xs.len() - 2 { swap_i, i = i, (i + 1) % (xs.len()) } // Stable sorting.
    for j in (xs.len() - 2) downto 0 { swap_j, j = (j + 1) % (xs.len()) }
    return xs
}

