def sort_by(m : Array[T], key : T -> Int): Array[T] = {
  // Create an empty array of the same size as the input array
  val newArray = new Array[T](m.length)
  // Create a local copy of the input array so we can modify it without modifying the original
  val inputArray = m.copy()

  // Initialize the element at index 0 to -1, which will be used as a sentinel value to determine where sorting begins
  val sentinel = -1

  // Main sorting loop that iterates over each element in the input array, one at a time
  for (i <- inputArray.indices) {
    // Find the index of the first element that is smaller than the current element in the input array, using binary search (O(log n))
    val j = binary_search(inputArray, i, sentinel, key)
    // If no such element exists, append the current element to the end of the sorted array without modifying its value (O(1))
    if (j >= sentinel) newArray(i) = inputArray(i) else {
      // Otherwise, move all elements smaller than the current element to the end of the sorted array (O(log n))
      for (k <- j until i by -1) newArray(k) = inputArray(k)
      // Then move the current element to its proper position in the sorted array (O(log n))
      binary_search(newArray, j + 1, i, key) += 1; newArray(j + 1) = inputArray(i); i += 1; } } return newArray}

