```js
function firstValidInput(inputs) {
    // Declare a variable to store the first valid input
    let validInput = null;

    // Iterate through the array of inputs and compare each input with the first valid input
    for (let i = 0; i < inputs.length; i++) {
        // If the current input is greater than or equal to the first valid input, it's not valid
        if (inputs[i] >= validInput) {
            continue;
        }

        // If the current input is less than the first valid input, it's the first valid input
        if (inputs[i] < validInput) {
            // Set the first valid input to the current input and break out of the loop
            validInput = inputs[i];
            break;
        }
    }

    // If the loop has been executed without finding a valid input, return null as an error message
    if (validInput === null) {
        return "Invalid input, first value must be greater than or equal to all others";
    } else {
        return "First valid input found at index: " + i;
    }    
}
