from collections import deque

class Queue:
    def __init__(self):
        self.stack_1 = deque()
        self.stack_2 = deque()

    def enqueue(self, item):
        self.stack_1.append(item)
        if not self.stack_2:
            self.stack_2.append(item)
        else:
            while self.stack_1:
