function longestStack(arr) {
  const n = arr.length;
  let max = 0;
  let i = 0;

  while (i < n) {
    if (arr[i] > max) {
      max = arr[i];
      i++;
    } else if (arr[i] == max && i < n - 1) {
      max = Math.max(arr[i + 1], max);
      i++;
    } else {
      max--; // reset the value back to zero when the stack is empty or not increasing any longer.
