def timsort(xs: List[Int]): List[Int] = {
  val len = xs.length
  if len <= 1 then xs else {
    val (yssorted, ys) = if xs.head < xs.tail then {
      val ys = xs.tail
      val (yssorted, ysnew) = timsort(ys)
