public class Solution {
    public boolean isPowerOfThree(int n) {
        int x = n;
        if (x == 0) {
            return false;
        }
        while (x > 0) {
            if (x % 3 != 0) {
                return false;
            }
            x /= 3;
        }
        return true;
    }
}
