std::string format_markdown_to_html(const std::string& markdown_text) {
    // Inicializar el string del resultado.
    std::string result;

    // Enumerar todos los caracteres del string del resultado y los recorrer.
    for (size_t i = 0; i < markdown_text.size(); i++) {
        // Si el caracter es un carácter especial de HTML, agregarlo al string del resultado.
        if (markdown_text[i] == '<' || markdown_text[i] == '>' || markdown_text[i] == '&' || markdown_text[i] == '"') {
            result += markdown_text[i];
        } else {
            // Si el caracter es un espacio o un tabulador, reemplazarlo por &nbsp; para HTML.
            if (iswspace(markdown_text[i]) || iswtab(markdown_text[i])) {
                result += "&nbsp;";
            } else {
                // Devolver el caracter tal cual.
                result += markdown_text[i];
            }
        }
    }
    return result;
}

