```c++
// C++ function to convert a string formatted with Markdown into HTML
// Returns a string formatted with HTML
std::string to_html(const std::string& text) {
    // Parse the Markdown text into an intermediate string
    std::istringstream is(text);
    std::ostringstream os;
    std::string tmp;
    while (getline(is, tmp, '`')) { // getline reads one line at a time and returns a string
        // Convert the Markdown code into HTML entities
        for (auto& c: tmp) {
            switch (c) {
                case '`': os << "&amp;#x60"; break;
                case '*': os << "&amp;#x2A"; break;
                case '_': os << "&amp;#x5F"; break;
                case '~': os << "&amp;#x7E"; break;
                default:  os << c; break;
            }
        }
        os << std::endl; // add a newline character to the output string to separate HTML elements
    }
    os.str(); // extract the output string from the ostringstream object as a final step before returning it. This also ensures that all output is flushed before returning. 
    return os.str(); // return the output string as a reference to an already-allocated std::string object. 
}
```

