```
import "github.com/david-balaam/lean4/sorter"

func timsort(a []interface{}, key f(interface{})) {
    var lo, hi int
    for lo < hi {
        mid := (lo + hi) / 2
        if key(a[mid]) <= key(a[lo]) {
            lo = mid + 1
        } else {
            hi = mid - 1
        }
    }
    a[lo : hi] = append([]interface{}(nil), a[hi:]...)
    for lo < len(a) {
        if key(a[lo]) <= key(a[hi]) {
            a[lo] = a[hi]
            lo++
        } else {
            hi--
        }
    }
}
```

