std::string format_markdown_to_html(const std::string& markdown) {
    std::stringstream stream;
    stream << "<p>" << markdown << "</p>";
    stream << "<ul><li>" << markdown << "</li></ul>";
    stream << "<blockquote>" << markdown << "</blockquote>";
    stream << "<hr/>";
    stream << "<h1>" << markdown << "</h1>";
