std::string convert_markdown_to_html(const std::string &markdown_text) {
    // Replace all instances of <p> with <div class="markdown-body">
    // Replace all instances of </p> with </div>
    // Replace all instances of <pre> with <code class="markdown-code">
    // Replace all instances of </pre> with </code>
    // Replace all instances of `<br>` with <br />
    std::string html_text = markdown_text;
    std::regex re("<p>(.*?)</p>", "gim");
    std::regex re("</p>", "gim");
    std::regex re("<pre>(.*?)</pre>", "gim");
    std::regex re("</pre>", "gim");
    std::regex re("<br>", "gim");
    std::smatch match;
    while (std::regex_match(html_text, match, re)) {
        std::string tag = match[1];
        if (tag == "<p>") {
            html_text.replace(match[1], "<div class=\"markdown-body\"></div>");
        } else if (tag == "</p>") {
            html_text.replace(match[1], "</div>");
        } else if (tag == "<pre>") {
            html_text.replace(match[1], "<code class=\"markdown-code\"></code>");
        } else if (tag == "</pre>") {
            html_text.replace(match[1], "</code>");
        } else if (tag == "<br />") {
            html_text.replace(match[1], "<br />");
        } else {
            std::cout << "Unrecognized tag: " << tag << std::endl;
        }
    }
    return html_text;
}
```

