def find_longest_common_subsequence(input_str1, input_str2):
    """Finds the longest common subsequence of two input strings.

    Args:
        input_str1 (str): The first input string.
        input_str2 (str): The second input string.

    Returns:
        (list[int]): The indices of the longest common subsequence of the two input strings, in increasing order.
    """
    if len(input_str1) < 2 or len(input_str2) < 2:
        return []
    length = min(len(input_str1), len(input_str2)) - 1
    output = []
    current = 0
    for i in range(length + 1):
        for j in range(length + 1):
            if input_str1[i] == input_str2[j]:
                if i >= len(input_str1) or j >= len(input_str2):
                    break
                current += 1
                output.append(current)
            else:
                break
        if i >= len(input_str1) and j >= len(input_str2):
            break
    return output[::-1]
