```cpp
#include <string>
#include <sstream>
#include <iostream>
#include <stdexcept>

std::string markdown_to_html(const std::string& markdown) {
  // Split the input string into an array of lines.
  std::vector<std::string> lines = split(markdown, '\n');

  // Create a stringstream to format the output.
  std::stringstream output;

  // For each line in the input string, format the output line.
  for (const auto& line : lines) {
    // If the line is empty, skip it.
    if (line.empty()) {
      continue;
    }

    // Convert the line to uppercase letters.
    line = uppercase(line);

    // Append the line to the output stringstream.
    output << line << "\n";
  }

  // Return a copy of the formatted output.
  return output.str();
}
```

