```lean
func timsort(A: Array[T]): Array[T] {
  if A.length == 0 { return [] }

  // trobar la primera posició de l'últim element que és menor que el darrer element
  var last = A[A.length-1]
  var idx = A.length - 1 - last.compare(last)

  // col·locar el darrer element al final de la col·lecció
  var tmp = A[idx]
  A[idx] = last

  // col·locar els elements menors que el darrer element en primer lloc
  for i in idx..0 by -1 {
    A[i] = A[i-1]
  }

  // col·locar els elements majors que el darrer element en últim lloc de la col·lecció (inversament)
  for i in (A.length-1)..0 by -1 {
    var tmp = A[i]
    A[i] = A[i-1]
    A[i-1] = tmp    
  }    

  return A    
}

