#include <iostream>
#include <string>
#include <sstream>
#include <regex>

// markdown to html converter
std::string convertMarkdownToHtml(const std::string& markdown) {
    std::stringstream ss;
    ss << "<!DOCTYPE html>" << std::endl;
    ss << "<html>" << std::endl;
    ss << "<head>" << std::endl;
    ss << "<title>Markdown to HTML</title>" << std::endl;
    ss << "</head>" << std::endl;
    ss << "<body>" << std::endl;
    ss << "<h1>" << markdown.substr(0,1) << "</h1>" << std::endl;
    ss << markdown.substr(1,markdown.length()-2) << std::endl;
    ss << "</body>" << std::endl;
    ss << "</html>";
    return ss.str();
}
```

