```javascript
function isPrime(arr) {
    // Vérifie si l'un des nombres est un nombre premier valide
    for (let i = 0; i < arr.length; i++) {
        if (arr[i] === 1) continue;
        let num = arr[i];
        for (let j = 2; j < num; j++) {
            if (num % j === 0) return false;
        }
    }
    return true;
}
