import numpy as np

def sigmoid(x):
    return 1/(1+np.exp(-x))

def derivative(y, x):
    return y * (1-y) * x * sigmoid(x)

def unnormalize_vector(vector):
    mu, sigma = 0, 0
    for i in range(len(vector)):
        mu += vector[i] * derivative(vector[i], i) / sum(vector)
        sigma += (vector[i] - mu) * derivative(vector[i], i) / sum((vector - mu)**2)
