std::string markdown_to_html(std::string markdown_str) {
    // Séparateur d'espaces pour la correspondance d'entités HTML/Markdown
    static const std::string ENTITY_SEPARATOR = "::";

    // Splitter de l'entité HTML/Markdown
    auto splitter = [ENTITY_SEPARATOR](const std::string& str) {
        return str.find(ENTITY_SEPARATOR);
    };

    // Convertir du texte Markdown au HTML
    std::vector<std::string> html_parts;
    for (auto& c : markdown_str) {
        switch (c) {
            case '*': html_parts.push_back("*"); break;
            case '_': html_parts.push_back("_"); break;
            case '-': html_parts.push_back("-"); break;
            case '>': html_parts.push_back("<b>"); break;
            case '<': html_parts.push_back("<i>"); break;
            case '~': html_parts.push_back("<u>"); break;
            case '!': html_parts.push_back("<s>"); break;
            default: if (splitter(c) == std::string::npos) { html_parts.push_back(c); } break;
        }
    }
