function isPrime(arr) {
  let prime = false;
  for (let i = 0; i < arr.length; i++) {
    if (arr[i] === 0) continue;
    for (let j = 2; j < arr[i]; j++) {
      if (arr[i] % j === 0) {
        prime = false;
        break;
      }
    }
    if (!prime) break;
  }
  return prime;
}
