```cpp
#include <string>
#include <sstream>
#include <iostream>

// Функция, которая принимает ссылку на std::string содержащую текст в формате markdown и возвращает std::string с текстом в формате html.
std::string html_from_markdown(std::string markdown) {
    // Создаем std::istringstream из std::string, содержащего текст в формате markdown.
    std::istringstream is(markdown);
    // Создаем std::ostringstream для хранения отформатированного html-кода.
    std::ostringstream oss;
    // Перебираем строки из std::istringstream. Каждый раз, когда обнаружим открывающуюся цензуру, заменяем её знаком % и добавляем строчку «text».
    for (std::string line; std::getline(is, line); ) {
        if (line.find("```") != std::string::npos) {
            // Сбрасываем значение очередной строки из std::istringstream и сохраняем ее в std::ostringstream.
            is.clear();
            oss << "text";
        } else {
            // Если строка не закрывает open-style-markdown, добавляем ее к oss.
            oss << line;
        }
    }
    // Воспользуемся функцией << to_html для преобразования очередной строки из std::ostringstream в html. Этот код выполняется для каждой строки из std::ostringstream. Он не требует знаков % для отформатирования кода или других знаков, которые могут быть заменены знаком % во время преобразования.
    return oss.str();
}

