    import Lean.Data.List.Iterator.iter_loop : iter_loop_with_guard [← Lean.Data.List] [← Lean.Data.List.Iterator] [← Nat] [← Nat] [← Nat] [← Nat]
    import Lean.Data.List.Iterator.iter_swap : iter_swap_with_guard [← Lean.Data.List] [← Lean.Data.List.Iterator] [← Nat] [← Nat] [← Nat] [← Nat]
