      await member.ban();
      return interaction.reply({
        content: `${user} has been banned.`,
        ephemeral: false,
      });
    } catch (error) {
      return interaction.reply({
        content: `I couldn't ban ${user}. Error: ${error}`,
        ephemeral: true,
      });
    }
  }
});

client.login('YOUR_DISCORD_BOT_TOKEN');
