#include <string>
#include <iostream>
#include <vector>
#include <algorithm>
using namespace std;

string convertMarkdownToHtml(string markdown) {
    string html = "";

    // Convert headers
    vector<string> headers = {"#", "##", "###", "####", "#####", "######"};
    transform(headers.begin(), headers.end(), back_inserter(html), [&](const string& h) {
        return "<h" + h + ">" + markdown.substr(0, h.length()) + "</h" + h + ">";
    });

    // Convert links
    size_t pos = markdown.find("[(.*?)](.*?)");
    while (pos != string::npos) {
        size_t endPos = markdown.find("]", pos);
        html += "<a href=\"" + markdown.substr(pos + 1, endPos - pos - 1) + "\">" + markdown.substr(pos + 1, endPos - pos - 1) + "</a>";
        pos = markdown.find("[", pos);
    }

    // Convert bold and italic
    pos = markdown.find("**");
    while (pos != string::npos) {
        size_t endPos = markdown.find("**", pos);
        html += "<strong>" + markdown.substr(pos + 2, endPos - pos - 2) + "</strong>";
        pos = markdown.find("**", endPos);
    }

    pos = markdown.find("*");
    while (pos != string::npos) {
        size_t endPos = markdown.find("*", pos);
        html += "<em>" + markdown.substr(pos + 1, endPos - pos - 1) + "</em>";
        pos = markdown.find("*", endPos);
    }

    return html;
}

