int binarySearch(int array[], int size, int element) {
    int start = 0;
    int end = size - 1;
    int mid;

    while (start <= end) {
        mid = (start + end) / 2;

        if (array[mid] == element) {
            return mid;
        } else if (array[mid] < element) {
            start = mid + 1;
        } else {
            end = mid - 1;
        }
    }

    return -1;
}

