class Queue:
    def __init__(self):
        self.in_stack = []  # stack to store items going into the queue
        self.out_stack = []  # stack to store items coming out of the queue

    def enqueue(self, item):
        self.in_stack.append(item)

    def dequeue(self):
        if not self.out_stack:
            while self.in_stack:
                self.out_stack.append(self.in_stack.pop())
        return self.out_stack.pop()

    def is_empty(self):
        return not self.in_stack and not self.out_stack

