#include <string>
#include <iostream>
using namespace std;

std::string convertMarkdownToHTML(const string& markdown) {
  string html;

  // Replace all markdown links with HTML links
  size_t pos = 0;
  while ((pos = markdown.find("https://", pos)) != string::npos) {
    size_t endPos = markdown.find(" ", pos);
    if (endPos == string::npos) {
      endPos = markdown.length();
    }
    string linkText = markdown.substr(pos, endPos - pos);
    html += "<a href=\"" + linkText + "\">" + linkText + "</a>";
    pos = endPos;
  }

  // Replace all markdown headers with HTML headers
  pos = 0;
  while ((pos = markdown.find("#", pos)) != string::npos) {
    size_t level = markdown[pos + 1] - '0';
    if (level >= 1 && level <= 6) {
      size_t endPos = markdown.find("\n", pos);
      if (endPos == string::npos) {
        endPos = markdown.length();
      }
      string headerText = markdown.substr(pos + 2, endPos - (pos + 2));
      html += "<h" + to_string(level) + ">" + headerText + "</h" + to_string(level) + ">";
      pos = endPos;
    } else {
      pos++;
    }
  }

  // Replace all markdown lists with HTML lists
  pos = 0;
  while ((pos = markdown.find("*", pos)) != string::npos) {
    size_t endPos = markdown.find("\n", pos);
    if (endPos == string::npos) {
      endPos = markdown.length();
    }
    string listText = markdown.substr(pos + 1, endPos - (pos + 1));
    html += "<ul><li>" + listText + "</li></ul>";
    pos = endPos;
  }

  return html;
}

int main() {
  string markdownText = "This is some *markdown* text. Here's a [link](https://example.com/) to an external site. This is a header #1 and this is a header #2. Here's a list:\n\n* Item 1\n* Item 2";
  string htmlText = convertMarkdownToHTML(markdownText);
  cout << "Markdown Text: \n" << markdownText << "\n\nHTML Text: \n" << htmlText << endl;
}
```
This function uses a combination of find() and substr() functions to parse the input Markdown text and replace the relevant Markdown syntax with HTML equivalents. The function can be called like this:

