#include <iostream>
#include <fstream>
#include <string>
using namespace std;

int main() {
    string word; // The word we are searching for
    int count = 0; // The number of occurrences found

    cout << "Enter the word you want to search for: ";
    cin >> word;

    // Open the input file
    ifstream infile("input.txt");

    // Read the input file line by line
    string line;
    while (getline(infile, line)) {
        // Count the number of occurrences of the word in the current line
        size_t pos = line.find(word);
        while (pos != string::npos) {
            count++;
            pos = line.find(word, pos + 1);
        }
    }

    // Close the input file
    infile.close();

    // Print the result
    cout << "The word '" << word << "' appears " << count << " times in the input file." << endl;

    return 0;
}
```

